/*
This script has been customized to my needs
It listens to a button with id 'export_all'
The divs specified here are 'gridd' and 'chartsn'
*/


$('#export_all').click(async function(){
   console.time('Exporting');  
   console.log('Exporting Started');    
   var doc=new jsPDF('p','pt','a4');
   var idg = document.querySelectorAll('#gridd');
   length_chart = idg.length;
   var x_cord = 25, y_cord = 100;
   var pr_gw,pr_gh,pr_xc,pr_yc;
   var pageWidth = doc.internal.pageSize.getWidth();
   var pageHeight = doc.internal.pageSize.getHeight();
   var grid_width=0,grid_height=0;         
   for (let i = 0; i < length_chart; i++) {
   chart = document.getElementsByClassName(idg[i].className);
   pr_gw=grid_width;
   pr_gh=grid_height;
   pr_xc=x_cord;
   pr_yc=y_cord;         
   grid_width=(idg[i].clientWidth - pageWidth) * (15/16);         
   grid_height=(idg[i].clientHeight/idg[i].clientWidth)*pageWidth;         
   if(i>0)
        {
            var cpH=pageHeight-pr_gh-pr_yc;
            if(grid_height>=cpH)
            {
            doc.addPage();
            }
            else 
            {
            y_cord=pr_gh+pr_yc+15;
            }
        }
   await html2canvas(chart,{scale:1}).then(function (canvas){
   var grid_img=Canvas2Image.convertToPNG(canvas);         
   gridData=grid_img.src;
   doc=watermark(doc);         
   doc.addImage(gridData, 'PNG', x_cord, y_cord,grid_width,grid_height);
   });

  }
   doc.addPage();
   var flag=0;        
   const ids = document.querySelectorAll('#chartsn');
   const length = ids.length;
   var chart_graph;
   var x_chart=10,y_chart=70;
   for (let i = 0; i < length; i++) {  
   chart_graph = document.getElementsByClassName(ids[i].className);   
   
   await html2canvas(chart_graph, {scale:1}).then(function (canvas) { 
   doc=watermark(doc);
   doc.addImage(canvas.toDataURL('image/png'), 'PNG', x_chart, y_chart,550,330); //screenshot add to pdf
   y_chart=y_chart+390;
   flag++;
   if(flag==2 && i < length-1)
   {
   doc.addPage();
   flag=0;
   y_chart=50;
   }
   });
   }
   doc=header(doc);
   doc=footer(doc);    
   doc.save('Grid_Report ' + Date.now() + '.pdf'); 
   console.timeEnd('Exporting');              
   });
   function footer(doc) {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();
    if (dd < 10) {
        dd = '0' + dd
    }
    if (mm < 10) {
        mm = '0' + mm
    }
    today = mm + '/' + dd + '/' + yyyy;
    var pageCount = doc.internal.getNumberOfPages();
    for (let i = 1; i <=pageCount; i++) {
        doc.setPage(i);
        doc.setFontSize(10);
        doc.text(10, 813, "Student Marks Report");
        doc.text(260, 813, "Page - " + doc.internal.getCurrentPageInfo().pageNumber + "/" + pageCount);
        doc.text(478, 813, "Printed at - " + today);
        doc.setLineWidth(0.5);
        doc.line(10, 800, 580, 800);
    }
    return doc;
}
function header(doc) {
    var pageCount = doc.internal.getNumberOfPages();
    for(let i=1; i <=pageCount; i++){
    doc.setPage(i);
    //var imgData = 'data:image/png;base64,';
   var cp=doc.internal.getCurrentPageInfo().pageNumber;
   doc.setPage(cp);
   var header=new Image();
   var dir=  "$header";      
                     
   var hdr;
   var canvas = document.createElement("canvas");
   canvas.width=122;
   canvas.height=110;        
   var ctx = canvas.getContext("2d");   
   header.src =dir;
   
   
   ctx.drawImage(header, 0, 0);                 
            
   hdr=canvas.toDataURL("image/png"); 
   
   
    doc.addImage(hdr, 'PNG', 120, 8, 35, 35); //logo
    doc.setFontSize(25);
    doc.text(160, 35, 'Cedar Mount Intl\' School');
    doc.setTextColor(150);
    doc.setFontSize(10);
    doc.text(90, 50, '29 Okania New Road, off Ada George, Port Harcourt, Rivers State, Nigeria. +23408181322838');
    doc.setLineWidth(0.5); 
    doc.line(10, 60, 580, 60);
    }        
    return doc;
}


   function watermark(doc) {
   var cp=doc.internal.getCurrentPageInfo().pageNumber;
   doc.setPage(cp);
   var wtrmek=new Image();
   
   var pageWidth = doc.internal.pageSize.getWidth();
   var pageHeight = doc.internal.pageSize.getHeight();
   var dir=  "$header";      
                     
   var wtr;
   var canvas = document.createElement("canvas");
   canvas.width=122;
   canvas.height=110;
   
   var ctx = canvas.getContext("2d");   
   
   wtrmek.src =dir;
   
   ctx.globalAlpha = 0.1;
   ctx.drawImage(wtrmek, 0, 0);                 
   ctx.globalAlpha = 1;         
   wtr=canvas.toDataURL("image/png"); 
   doc.addImage(wtr, 'PNG', (pageWidth/2)-55, (pageHeight/2)-35,122,110);        
   
      return doc;         
   }
